let countDownDate=new Date("Aug 25, 2021 12:00:00").getTime(),countDown=setInterval(()=>{let now=(new Date).getTime(),timeLeft=countDownDate-now,days=Math.floor(timeLeft/864e5),hours=Math.floor(timeLeft%864e5/36e5),minutes=Math.floor(timeLeft%36e5/6e4),seconds=Math.floor(timeLeft%6e4/1e3);document.getElementById("days").innerHTML=days+"d ",document.getElementById("hours").innerHTML=hours+"h ",document.getElementById("mins").innerHTML=minutes+"m ",document.getElementById("secs").innerHTML=seconds+"s ",timeLeft<0&&(clearInterval(countDown),document.getElementById("days").innerHTML="",document.getElementById("hours").innerHTML="",document.getElementById("mins").innerHTML="",document.getElementById("secs").innerHTML="",document.getElementById("end").innerHTML="SALES ELAPSED!!")},1e3);